// components/PostDetail.js
import React from 'react';

const PostDetail = ({ post, onBack }) => {
  return (
    <div>
      <button onClick={onBack} className="text-blue-500 mb-4">Back to Search Results</button>
      <h2 className="text-2xl font-bold mb-4">{post.title}</h2>
      <p className="text-gray-600 mb-2">
        <strong>Author:</strong> {post.author}
      </p>
      <p className="text-gray-600 mb-2">
        <strong>Points:</strong> {post.points}
      </p>
      <p className="text-gray-600 mb-4">
        <strong>Created At:</strong> {post.created_at}
      </p>
      <p className="mb-4">{post.text}</p>
      <ul>
        {post.children.map((comment) => (
          <li key={comment.id} className="mb-2">{comment.text} <br/> <hr/></li>
        ))}
      </ul>
    </div>
  );
};

export default PostDetail;
